<?php /* * */ get_header(); ?><div id="wrapper">	<div class="articlebody">	<?php if (have_posts()) : while (have_posts()) : the_post(); ?>		<div class="post" id="post_<?php the_ID(); ?>">			<div class="entry">				<?php the_content(); ?>				<div>					<div class="podcast-archive-container">					<?php 												$args = array( 'post_type' => 'ghg_podcast', 'posts_per_page' => 10 );						$loop = new WP_Query( $args );						while ( $loop->have_posts() ) : $loop->the_post();							echo '<div class="podcast-archive">'; ?>								<h6><a href="<?php the_permalink() ?>" rel="bookmark" title="Permanent Link to <?php the_title_attribute(); ?>"><?php the_title(); ?> - <?php the_time('l, F jS, Y') ?></a></h6>								<?php 									// post meta									$downloadlink = get_post_meta($post->ID, 'enclosure', true); 									$podcastexcerpt = get_post_meta($post->ID, 'podcast_excerpt', true);  // optional																		if ( !empty($podcastexcerpt) ) {										echo $podcastexcerpt;									}									the_content(); 								?>															<?php echo '</div>';						endwhile;										?>					</div>					<img src="<?php bloginfo('template_url'); ?>/images/podcast-dave.jpg" alt="Podcast Archive" class="alignright" />				</div>			</div>		</div>	<?php endwhile; ?>	<?php else : ?>		<div class="post">			<div class="entry">				<h3>Post not found</h3>			</div>		</div>	<?php endif; ?>	</div>	<?php include (TEMPLATEPATH . '/sidebar.php'); ?>	<div class="clearboth"></div></div><?php get_footer(); ?>